hudson.tasks.Shell.command:
#!/usr/bin/env node

var fs = require('fs');
var path = require('path');
var rimraf = requireWS('rimraf');
var exists = fs.existsSync;

var dir = process.env.UPSTREAM_WORKSPACE;
var numberOfBuilds = parseInt(process.env.NUMBER_OF_BUILDS, 10);


console.log('Cleaning up workspace:', dir);

if (!exists(dir)) throw new Error('Cannot load dir:' + dir);
if (!exists(path.join(dir, 'results'))) throw new Error('Cannot load dir:' + path.join(dir, 'results'));

var files = fs.readdirSync(path.join(dir, 'results'));
var max = Math.max.apply(null, files);
var min = max - numberOfBuilds;

console.log('max', max);
console.log('min', min);

files.forEach(function(file) {
  file = parseInt(file, 10);
  if (isNaN(file)) return;


  if (file < min) {
    console.log('Results dir %s is older than %s. Removing.', file, min);
    rimraf.sync(path.join(dir, 'results', file + ''));
    return;
  }

});


function requireWS(file) {
  var env = process.env;
  return require(env.WORKSPACE ? path.join(env.WORKSPACE, 'node_modules', file) : file);
}
