hudson.tasks.Shell.command:
#!/usr/bin/env bash
RESULT_DIR=results/$BUILD_NUMBER URLS="$PERF_URLS" BROWSERS=$(node -pe 'b = process.env.SAUCE_BROWSERS; b.split(/,\s?/).join(" ")')
# URL_KEY=$(node -pe 'process.argv.slice(1)[0].replace(/(^https?:\/\/)|(\/$)/g, "").replace(/(\/|\?|-|&|=|\.)/g, "_")' "$url");
for browser in $BROWSERS; do mkdir -p $RESULT_DIR/$browser echo saucelabs-browsertime $PERF_URLS --browser $browser --db index.json saucelabs-browsertime $PERF_URLS --browser $browser --db index.json > $RESULT_DIR/$browser/metrics.json done
